var XMLHttpReq; //创建XMLHttpRequest对象 function createXMLHttpRequest() { if(window.XMLHttpRequest) { //Mozilla 浏览器 XMLHttpReq = new XMLHttpRequest(); } else if (window.ActiveXObject) { // IE浏览器 try { XMLHttpReq = new ActiveXObject("Msxml2.XMLHTTP"); } catch (e) { try { XMLHttpReq = new ActiveXObject("Microsoft.XMLHTTP"); } catch (e) {} } } } //发送请求函数 function sendFavRequest(obj, submitAction, code) { if (submitAction == 'addtomyfav') { strTips = '确实要将 长盛同庆A(150006) 添加到我关注的基金吗?'; } else if (submitAction == 'removefrommyfav') { strTips = '确实要将 长盛同庆A(150006) 从我关注的基金中删除吗?'; } if(!window.confirm(strTips)){return false;} createXMLHttpRequest(); var url = "http://www.ourku.com/myFundFav.php?submitAction="+submitAction+"&addcode=" + code; XMLHttpReq.open("GET", url, true); XMLHttpReq.onreadystatechange = processFavResponse;//指定响应函数 XMLHttpReq.send(null); // 发送请求 } // 处理返回信息函数 function processFavResponse() { if (XMLHttpReq.readyState == 4) { // 判断对象状态 if (XMLHttpReq.status == 200) { // 信息已经成功返回,开始处理信息 FavDisplayHot(); } else { //页面不正常 window.alert("您所请求的页面有异常。请重新刷新本页!"); } } } function FavDisplayHot() { var str = XMLHttpReq.responseText; var info = str.split(','); if (info[0] == 1) { if (info[1] == 1) { if (info[2] == 1) { if (info[3] == 1) { alert('添加成功!'); document.getElementById('btnfavzone').innerHTML = '
从我关注的基金中删除'; } else if(info[3] == -1) { alert('删除成功!'); document.getElementById('btnfavzone').innerHTML = '
添加到我关注的基金'; } } else { alert('操作失败!'); } } else { alert('代码错误,请联系管理员!'); } } else { alert('请先登录或注册会员!'); } }

